import React from "react";
import {useParams}from 'react-router-dom'

const Questiondetails = () => {
    const { id }=useParams()
    var questionsList = [
        {
            _id: 1,
            upvotes: 3,
            downvotes: 2,
            noOfAnswers: 2,
            QuestionTitle: "What is a Function?",
            questionBody: "It meant to be",
            questionTags: ["java", "node js", "react js", "mongodb"],
            userPosted: "mano",
            userId: 1,
            askedOn: "jan 1",
            answer:[{
                answerBody: "Answer",
                userAnswered: "Kumar",
                answeredOn:"jan 2",
                userId:2,
        }]
        },
        {
            _id: 2,
            upvotes: 3,
            downvotes: 2,
            noOfAnswers: 0,
            QuestionTitle: "What is a Function?",
            questionBody: "It meant to be",
            questionTags: ["javaScript", "R", "Python"],
            userPosted: "mano",
            userId: 2,
            askedOn: "jan 1",
            answer:[{
                answerBody: "Answer",
                userAnswered: "Kumar",
                answeredOn:"jan 2",
                userId:2,
        }]
        },
        {
            _id: 3,
            upvotes: 3,
            downvotes: 2,
            noOfAnswers: 0,
            QuestionTitle: "What is a Function?",
            questionBody: "It meant to be",
            questionTags: ["javaScript", "R", "Python"],
            userPosted: "mano",
            userId: 3,
            askedOn: "jan 1",
            answer:[{
                answerBody: "Answer",
                userAnswered: "Kumar",
                answeredOn:"jan 2",
                userId:2,
        }]
        },
    ];
    
    return (
        <div className="question-details-page">

        {
            questionsList===null?
            <h1>Loading...</h1>:
            <>
                {
                    questionsList.filter(question => question._id === id).map(question)
                }
            </>
        }
            
        </div>
    )
};

export default Questiondetails;
